package com.pizzastore.msg.controller; import org.springframework.web.bind.annotation.*; import java.util.ArrayList; import java.util.List; import java.util.Map; @RestController @RequestMapping("/msg") public class MessageController { private final List<Map<String,String>> inbox = new ArrayList<>(); @PostMapping("/send") public Map<String,String> send(@RequestBody Map<String,String> body){ inbox.add(body); return Map.of("status","sent"); } @GetMapping("/inbox") public List<Map<String,String>> inbox(){ return inbox; } }
