package com.pizzastore.user.entity; import javax.persistence.*; @Entity public class MenuItem { @Id @GeneratedValue private Long id; private String name; private String category; private String description; private Double price; private Boolean available = true; public MenuItem() {} public MenuItem(String name,String category,String description,Double price){this.name=name;this.category=category;this.description=description;this.price=price;} public Long getId(){return id;} public void setId(Long id){this.id=id;} public String getName(){return name;} public void setName(String name){this.name=name;} public String getCategory(){return category;} public void setCategory(String category){this.category=category;} public String getDescription(){return description;} public void setDescription(String description){this.description=description;} public Double getPrice(){return price;} public void setPrice(Double price){this.price=price;} public Boolean getAvailable(){return available;} public void setAvailable(Boolean available){this.available=available;} }
