package com.pizzastore.user.entity; import javax.persistence.*; import java.time.LocalDateTime; import java.util.List; @Entity(name="orders") public class OrderEntity { @Id @GeneratedValue private Long id; private Long userId; private String status; private Double totalAmount; private LocalDateTime createdAt = LocalDateTime.now(); @OneToMany(cascade = CascadeType.ALL) private List<OrderItem> items; public Long getId(){return id;} public void setId(Long id){this.id=id;} public Long getUserId(){return userId;} public void setUserId(Long userId){this.userId=userId;} public String getStatus(){return status;} public void setStatus(String status){this.status=status;} public Double getTotalAmount(){return totalAmount;} public void setTotalAmount(Double totalAmount){this.totalAmount=totalAmount;} public LocalDateTime getCreatedAt(){return createdAt;} public void setCreatedAt(LocalDateTime createdAt){this.createdAt=createdAt;} public List<OrderItem> getItems(){return items;} public void setItems(List<OrderItem> items){this.items=items;} }
