package com.pizzastore.user.entity; import javax.persistence.*; @Entity public class OrderItem { @Id @GeneratedValue private Long id; private Long menuItemId; private Integer quantity; private Double price; public Long getId(){return id;} public void setId(Long id){this.id=id;} public Long getMenuItemId(){return menuItemId;} public void setMenuItemId(Long menuItemId){this.menuItemId=menuItemId;} public Integer getQuantity(){return quantity;} public void setQuantity(Integer quantity){this.quantity=quantity;} public Double getPrice(){return price;} public void setPrice(Double price){this.price=price;} }
